import React, { Component } from 'react';
import { ProductConsumer } from './context';

class Details extends Component {
    render() {
        return (
            <ProductConsumer>
                {value  => {
                    const {id,company,img,info,price,title,inCart} = value.detailProduct;
                    return(
                        <div className="conatiner py-5">
                            <div className="row">
                                <div className="col-10 mx-auto text-center text-slanted my-5">
                                    <h1 className="ProductTitle">{title}</h1>
                                </div>
                            </div>
                            <div className="row">
                                <div className="col-10 mx-auto col-md-6 my-3">
                                    <img src={img} className="img-fluid" alt="product"/>
                                </div>
                                <div className="col-10 mx-auto col-md-6 my-3 text-capitalize">
                                    <h2 className="mb-2 mt-3 text-title text-uppercase text-muted">{title}</h2>
                                    <h4>Company: <span className="text-uppercase">{company}</span></h4>
                                    <h4><strong>price: </strong><span>$</span>{price}</h4>
                                    <p className="text-capitalize mt-3 mb-0 font-weight-bold">Description: </p>
                                    <p className="text-muted lead">{info}</p>
                                </div>
                            </div>
                        </div>
                    );
            }}
            </ProductConsumer>
        );
    }
}

export default Details;
