import React, { Component } from 'react';
import { ProductConsumer } from './context';
import {Link} from 'react-router-dom';

class ModalShop extends Component {
    render() {
        return (
            <ProductConsumer>
                {(value) => {
                    const {modalOpen, modalClose} = value;
                    const {title, img, price} = value.ModalProduct;
                    if(!modalOpen){
                        return null;
                    }else{
                        return(
                            <div className="modalContainer">
                                <div className="row">
                                    <div className="col-8 mx-auto col-md-6 col-lg-4 text-center text-capiltalize" id="modal">
                                        <h5>Item added to the cart</h5>
                                        <img src={img} alt="product image" className="img-fluid"/>
                                        <h5>{title}</h5>
                                    </div>
                                </div>
                            </div>
                        )
                    }
                }}
            </ProductConsumer>
        );
    }
}

export default ModalShop;
