import React from 'react';
import {Link} from 'react-router-dom';

const CartButton = ({value}) => {
    const {cart, total} =value;
    return (
        <button className="mycartbtn"><Link to="/cart" ><i className="mycart fas fa-cart-plus"/>My Cart ( {cart.length} ) : ${total}</Link></button>
    );
};

export default CartButton;
