import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button } from 'reactstrap';



class Product extends Component {
    render() {
        const {title,img,price,inCart} = this.props.product;
        return (
        <div className="row">
            <div className="responsive">
                <div className="gallery">
                    <div className="img-container p-4">
                        <Link to="/details" ><img src={img} alt="" className="card-img-top"/></Link>
                        <button className="cart-btn" disabled={inCart ?true:false}>
                            {inCart? (<p className="mb-0 text-capitalize" disabled>{""}In Cart</p>):(<i className="fas fa-cart-plus"></i>)}
                        </button>
                    </div>
                    <div className="card-footer d-flex justify-content-between">
                        <p className="mb-0 align-self-center">{title}</p>
                        <h5 className="mb-0"><span className="mr-1">$</span>{price}</h5>
                    </div>
                </div>
            </div>
            <Card>
                <CardImg top width="100%" src="https://placeholdit.imgix.net/~text?txtsize=33&txt=318%C3%97180&w=318&h=180" alt="Card image cap" />
                <CardBody>
                    <CardTitle>Card title</CardTitle>
                    <CardSubtitle>Card subtitle</CardSubtitle>
                    <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                    <Button>Button</Button>
                </CardBody>
            </Card>
        </div>
        );
    }
}

export default Product;
